import { useState } from 'react/cjs/react.development';
import './App.css';


import Accordian from './Accordian';
import { useEffect } from 'react';
const data=[
  {
      que:"How are yoy?",
      ans:"Chaka Cha.k"
  },
  {
      que:"what are u doing these days ?",
      ans:"I am doing trainning from 10x Academy of Full-stack-development."

  },
  {
      que:"What is carrer possiblities is ur bootcamp ?",
      ans:"Carrer possiblities is really good in my bootcamp looking forward to sit in placements."
  }

]


function App() {




  const [dir, setDirn] = useState("up")



  return (
    <>
      <div className='main'>

        <div className='btns'>
          <button onClick={()=>setDirn("up")}><i class="fas fa-angle-down"></i></button>
          <button onClick={()=>setDirn("down")}><i class="fas fa-angle-up"></i></button>
          <button onClick={()=>setDirn("left")}><i class="fas fa-angle-left"></i></button>
          <button onClick={setDirn("right")}><i class="fas fa-angle-right"></i></button>

        </div>
        {
          data.map((curElem,index)=>(
            //console.log(curElem.ans,index)
            <Accordian  que={curElem.que} ans={curElem.ans} index={index} key={index} dir={dir}/>
          ))
        }
      </div>
    </>
  );
}

export default App;
