    if (dir === "up") {
      setDir("up")
    } else if (dir === "down") {
      setDir("down")

    } else if (dir === "left") {
      setDir("left")
    } else if (dir === "right") {
      setDir("{right")

    }
  }


  return (
    <>
      <div className='main'>

        <div className='btns'>
          <button onClick={setDirn("up")}><i class="fas fa-angle-down"></i></button>
