import React from "react";
import { useState } from "react/cjs/react.development";


const Accordian =({que,ans,index,key,dir})=>{
    console.log(que)
    const [dirn,setDir]=useState("up")
   {
        if (dir === "up") {
          setDir("up")
        } else if (dir === "down") {
          setDir("down")
      
        } else if (dir === "left") {
          setDir("left")
        } else if (dir === "right") {
          setDir("{right")
      
        }
      }
    
    return(
        <>
        <h1>{que}</h1>
        <h1>{ans}</h1>
        <h1>{index}</h1>
        
        </>
    )
}
export default Accordian
