import React, { useEffect, useState } from "react";

const Accordian1=(data)=>{
    const [isActive,setActive]=useState([])
    useEffect(()=>{
        const byDefault=[]
        for(let i=0;i<data.length;i++){
            byDefault.push(false)

        }
        setActive([])
    })
    return(
        <>
        <h2>{data.text}</h2>
        <h2>{data.title}</h2>
        
        </>
    )
}

export default Accordian1
