import React, { useEffect, useState } from "react";

const Accordian1=(data)=>{
    const [isActive,setActive]=useState([])
    useEffect((data)=>{
        const byDefault=[]
        for(let i=0;i<data.length;i++){
            byDefault.push(false)

        }
        setActive(byDefault)

    },[])
    const changeState =(index)=>{
        let newState=[]
        for(let i=0;i<data.length;i++){
            if(parseInt(index)===i){
                newState.push(true)


            }else{
                newState.push(false)
            }

        }
        setActive(newState)
        

    }
    return(
        <>
        <h2 onClick={()=> changeState(data.index) } > {data.text}</h2>
        { isActive[data.index] && <h2>{data.title}</h2>}
        
        </>
    )
}

export default Accordian1
