import React, { useEffect, useState } from "react";

const Accordian1=()=>{
    const data=[
        {
          title:"Tell something",
          text:"hlo how are u?"
        },
        {
          title:"Tell something",
          text:"hlo how are u?"
        },
        {
          title:"Tell something",
          text:"hlo how are u?"
        },
        {
          title:"Tell something",
          text:"hlo how are u"
        }
      ]
    const [isActive,setActive]=useState([])
    useEffect(()=>{
        const byDefault=[]
        for(let i=0;i<data.length;i++){
            byDefault.push(false)

        }
        setActive(byDefault)

    },[])
    const changeState =(index)=>{
        let newState=[]
        for(let i=0;i<data.length;i++){
            if(parseInt(index)===i){
                newState.push(true)


            }else{
                newState.push(false)
            }

        }
        setActive(newState)
        

    }
    return(
        <>
        <h1>
        Hlo
        </h1>
        {    
        data.map((curElm,index)=>{
            
            (
                <>
            <h2 onClick={()=> changeState(index) } > {curElm.title}</h2>
            { isActive[index] && <h2>{curElm.text}</h2>}
            </>
            )


        })  
       
        }
        
        </>
    )
}

export default Accordian1
